class Solution:
    def minOperationsMaxProfit(self, customers: List[int], boardingCost: int, runningCost: int) -> int:
        if 4 * boardingCost < runningCost:
            return -1

        more_than_once = list(filter(lambda x: x > 4, customers))
        customers += [0] * (ceil(sum(more_than_once)/4) - len(more_than_once))
        n = len(customers)
        total_profit = max_profit = min(customers[0], 4) * boardingCost - runningCost
        if n > 1:
            customers[1] += max(0, customers[0]-4)

        res = 1
        for i in range(1, n):
            profit = min(customers[i], 4) * boardingCost - runningCost
            total_profit += profit
            if i + 1 < n:
                customers[i+1] += max(0, customers[i]-4)
            if total_profit > max_profit:
                max_profit = total_profit
                res = i + 1

        return res if max_profit > 0 else -1
