            profit += boarding * boarding_cost - running_cost;
            i += 1;
            if profit > max_profit {
                max_profit = profit;
                max_profit_rotation = i;
            }
        }
        if max_profit > 0 {
            max_profit_rotation
        } else {
            -1
        }
    }
}
