public class Solution {
    public int MinOperationsMaxProfit(int[] customers, int boardingCost, int runningCost) {
        int group = 0;
        int totalRotations = 1;
        int currentProfit = 0;
        (int rotation, int profit) best = (0,0);

        // if enough customers have arrived, we can do multiple groups to make 4 people
        while (group < customers.Length) {
            var boardingNumber = 0;
            if (customers[group] > 4){
                boardingNumber = 4;
                customers[group]-= boardingNumber;
            } else {
                boardingNumber = customers[group];
                group++;

                while (group < totalRotations && group < customers.Length && boardingNumber < 4){
                    if (customers[group] > 0) { 
                        customers[group]--;
                        boardingNumber++;
                    }
                    if (customers[group] == 0)
                        group++;
                }
            }

            currentProfit+= (boardingCost * boardingNumber - runningCost);

            if (currentProfit > best.profit)
                best = (totalRotations, currentProfit);

            totalRotations++;
        }

        return best.rotation > 0 ? best.rotation : -1;
    }
}
