class Solution {
public:
    int minOperationsMaxProfit(vector<int>& customers, int BC , int RC) {
        int maxProfit = INT_MIN , profit = 0 , rots = 1 , maxRots = 0 ;
        int waiting = 0 , totalPeople = 0 ;

        for(int i = 0 ; i < customers.size() ; ++i ){
            int onBoard = min(4,customers[i]) ;
            totalPeople += onBoard ;
            waiting += abs(customers[i] - onBoard) ;

            if(onBoard < 4){
                int diff = abs(4 - onBoard) ;
                int take = min(waiting,diff) ;
                waiting -= take ; totalPeople += take ; onBoard += take ; 
            }
            profit = totalPeople * BC  - rots * RC ;
            if(maxProfit < profit) maxProfit = profit , maxRots = rots ;
            ++rots ;
        }
        while(waiting){
            int onBoard = min(4,waiting) ;
            totalPeople += onBoard ;
            waiting -= onBoard ;
            profit = totalPeople * BC - rots *RC ;
            if(maxProfit < profit) maxProfit = profit , maxRots = rots ;
            ++rots ;
        } 
        return maxProfit > 0 ? maxRots : -1 ;
    }
};
