/**
 * @param {number[]} customers
 * @param {number} boardingCost
 * @param {number} runningCost
 * @return {number}
 */
var minOperationsMaxProfit = function(customers, boardingCost, runningCost) {
    if (runningCost >= boardingCost * 4)    return -1


    let earning = 0
    let cost = 0
    let waiting = 0
    let profit = 0
    let maxProfit = 0
    let bestTime = -1


    for (const val of customers.values()) {
        waiting += val

        if (waiting > 4) {
            earning += 4 * boardingCost
            waiting -= 4
        } else {
            earning += waiting * boardingCost
            waiting = 0
        }

        cost += runningCost

        const time = cost / runningCost
        profit = earning - cost

        if (profit >= maxProfit) {
            maxProfit = profit
            bestTime = time
        }
    }


    if (waiting > 0) {
        let lastSize = waiting % 4
        if (lastSize === 0) lastSize = 4
        const rotateCount = Math.ceil(waiting / 4)


        cost += rotateCount * runningCost
        earning += waiting * boardingCost
        waiting = 0

        const time = cost / runningCost
        profit = earning - cost

        if (profit > maxProfit) {
            maxProfit = profit
            bestTime = time
        }


        const lastDiff = lastSize * boardingCost - runningCost
        if (lastDiff <= 0 && rotateCount > 1) {
            maxProfit -= lastDiff
            bestTime--
        }
    }


    if (maxProfit <= 0) return -1
    return bestTime
};
