class Solution {
    func minOperationsMaxProfit(_ customers: [Int], _ boardingCost: Int, _ runningCost: Int) -> Int {
        var waiting = 0, profit = 0, maxProfit = 0, turn = 0, result = -1
        func run() {
            let ongoing = min(waiting, 4)
