            profit += ongoing * boardingCost - runningCost
            turn += 1
            if profit > maxProfit {
                maxProfit = profit
                result = turn
            }
        }
        for customer in customers {
            waiting += customer
            run()
        }
        while waiting > 0 {
            run()
        }
        return result
    }
