            if(customers[i]>=4){
                waiting+=customers[i]-4;
                boarded+=4;
                profit=(boarded*boardingCost)-(running*runningCost);
                if(profit>max){
                    max=profit;
                    gain_rotation=running;
                }
            }else{
                if(waiting>=4-customers[i]){
                    boarded+=4;
                    waiting-=4-customers[i];
                }else{
                    boarded+=customers[i];
                }
                profit=(boarded*boardingCost)-(running*runningCost);
                if(profit>max){
                    max=profit;
                    gain_rotation=running;

