class Solution {
    public int minOperationsMaxProfit(int[] customers, int boardingCost, int runningCost) {
		// if running cost is too high, it's not possible to make profit
        if(runningCost>=4*boardingCost) return -1;
        int profit=0,maxProfit=0,waitingCnt=0,opCnt=0,result=-1;
        while(true) {
			// running cost
            profit-=runningCost;
            if(opCnt<customers.length) {
				// add customer to waiting list
                waitingCnt+=customers[opCnt];
            }

			// add operation count
            opCnt++;

			// onboard and make profit
            var onboard=Math.min(4,waitingCnt);
            waitingCnt-=onboard;
            profit+=onboard*boardingCost;

			// check if make better profit 
            if(profit>maxProfit) {
                maxProfit=profit;
                result=opCnt;
            }

			// quit if there is no more new customer and waiting list is empty
            if(waitingCnt==0&&opCnt>=customers.length) break;
        }
        return result;
    }
}
