class Solution {
    public int minOperationsMaxProfit(int[] customers, int boardingCost, int runningCost) {
        int res = -1, profit = 0, maxProfit = 0, currCustomers = 0, rotations = 0;

        while (rotations < customers.length || currCustomers > 0) {
            if (rotations < customers.length)
                currCustomers += customers[rotations];
            int board = currCustomers >= 4 ? 4 : currCustomers;
            profit += board * boardingCost - runningCost;

            if (profit > maxProfit) {
                maxProfit = profit;
                res = rotations + 1;
            }
            currCustomers -= board;
            rotations++;
        }
        return res;
    }
}
