        let mut run = 0;
        let mut max_run = 0;
        let mut profit = 0;
        let mut max_profit = 0;
        let mut sum = 0;
        let mut i = 0;
        while sum > 0 || i < customers.len() {
            if i < customers.len() {
                sum += customers[i];
                i += 1;
            }
            let board = if sum > 4 { 4 } else { sum };
            sum -= board;
            profit += board * boarding_cost - running_cost;
            run += 1;
            if profit > max_profit {
                max_profit = profit;
                max_run = run;
            }
