func minOperationsMaxProfit(customers []int, boardingCost int, runningCost int) int {
	profit := math.MinInt32
	people := 0
	wait := 0
	maxRound := 0
	for i, c := range customers {
		wait += c
		people += c
		if wait <= 4 {
			wait = 0
		} else {
			wait -= 4
		}
		curr, round := currentProfit(wait, people, i+1, boardingCost, runningCost)
		if curr > profit {
			profit = curr
			maxRound = round
		}
	}
	if profit <= 0 {
		return -1
	}
	return maxRound
}

func currentProfit(wait, people, round, boardingCost, runningCost int) (int, int) {
	round += wait/4
	profit := (people - wait%4) * boardingCost - round * runningCost
	if wait % 4 != 0 {
		temp := people * boardingCost - (round+1) * runningCost
		if temp > profit {
			return temp, round+1
		}
	}
	return profit, round
}
