class Solution {
public:
    int minOperationsMaxProfit(vector<int>& customers, int boardingCost, int runningCost) {
        int sum = 0;
        int l = customers.size();
        for(int i = 0; i < l; i++){
            sum += customers[i];
        }
        int shift = sum / 4;
        int remain = sum - (shift * 4);
        //cout<<"shift "<<shift<<endl;
        //cout<<"remain "<<remain<<endl;
        int ans = INT_MIN;
        int j, index = 0;
        for(j = 1; j <= shift; j++){
            int cost = 4 * j * boardingCost - j * runningCost;
            if(ans < cost){
                ans = cost;
                index = j;
            }
        }
        if(remain > 0){
            int remainCost = sum * boardingCost - j *  runningCost;
            if(ans < remainCost){
                ans = remainCost;
                index = j;
            }
        }
        //cout<<"ans "<<ans<<endl;
        return ans < 0 ? -1 : index;
    }
};
