class Solution {
public:
    int minOperationsMaxProfit(vector<int>& customers, int boardingCost, int runningCost) {
        int n=customers.size();
        if(n==0)
        {
            return -1;
        }
        int sum=0;
        int ind=-1;
        int mx=0;
        int total=0;
        for(int i=0;i<n;i++)
        {
            total=total+customers[i];
            int profit=0;
            int temp;
            if(total>=4)
            {
                temp=4;
                total=total-4;
            }
            else
            {
                temp=total;
                total=0;
            }
            sum=sum+temp;
            profit=sum*boardingCost-(i+1)*runningCost;
            if(profit>mx)
            {
                mx=profit;
                ind=i+1;
            }
        }
        int round=n+1;
        while(total>0)
        {
            int profit=0;
            int temp;
            if(total>=4)
            {
                temp=4;
                total=total-4;
            }
            else
            {
                temp=total;
                total=0;
            }
            sum=sum+temp;
            profit=sum*boardingCost-(round)*runningCost;

            if(mx<profit)
            {
                mx=profit;
                ind=round;
            }
            round++;
        }
        if(ind==-1)
        {
            return -1;
        }
        else
        {
        return ind;
        }
    }
};

