const minOperationsMaxProfit = ( customers, boardingCost, runningCost ) => {
    let boarding, i = 0
    let sum = 0, waiting = 0
    let max = 1, turn = -1

    while ( waiting || i < customers.length ) {
        waiting += customers[ i++ ] || 0
        boarding = Math.min( 4, waiting )

        sum += boardingCost * boarding - runningCost
        waiting -= boarding

        if ( sum > max ) {
            max = sum
            turn = i
        }
    }

    return turn
}
