class Solution {
    public int minOperationsMaxProfit(int[] customers, int boardingCost, int runningCost) {

       int wait=0,customer=0,mx=Integer.MIN_VALUE,currentProfit=0,round=-1;
        for(int i=1;i<=customers.length||wait>0;i++){
            wait=wait+ (i-1 < customers.length ? customers[i-1]:0);//if greater than length than no increment in wait as queue of customer ends

            if(wait>=4){  //the logic of customer waiting
             customer+=4;
                wait-=4;
            }else{                 //add current wait to customer 
                customer+=wait;
                wait=0;
            }
            currentProfit = customer*boardingCost - i*runningCost; //cuurrent profit
            if(mx<currentProfit){ 
                mx=currentProfit;
                round=i;      //round is the iteration times i.e the index or number of iteration with highest profit
            }
        }
        return currentProfit > 0 ? round:-1; //for last testcase negative profit than return -1

  }
}

