class Solution {
    func minOperationsMaxProfit(_ customers: [Int], _ boardingCost: Int, _ runningCost: Int) -> Int {
        var maxProfit = 0
        var maxRotation = 0
        var waitingCustomers = 0
        var profit = 0
        var rotation = 0

        for _customers in customers{
            waitingCustomers += _customers
            helper(&waitingCustomers, &maxProfit, &maxRotation, &profit, &rotation, boardingCost, runningCost)
        }


        while waitingCustomers > 0{
            helper(&waitingCustomers, &maxProfit, &maxRotation, &profit, &rotation, boardingCost, runningCost)
        }

        return maxRotation > 0 ? maxRotation : -1
    }

    private func helper(_ waitingCustomers: inout Int, _ maxProfit: inout Int, _ maxRotation: inout Int, _ profit: inout Int, _ rotation: inout Int, _ boardingCost: Int, _ runningCost: Int){
        let onboardingCustomers = min(waitingCustomers, 4)
        waitingCustomers -= onboardingCustomers            
        profit += onboardingCustomers * boardingCost - runningCost
        rotation += 1
        if maxProfit < profit{
            maxProfit = profit
            maxRotation = rotation
        }
    }
}
