class Solution {
    public int minOperationsMaxProfit(int[] customers, int boardingCost, int runningCost) {
        int ans = 0; 
        int moves = 1;
        int balance = 0;
        int max = 0;
        int p = 0;
        for(int i=0; i< customers.length; i++){
            balance += customers[i];
            int px = Math.min(balance, 4);
            balance = balance - px;
            p+=px;
            int curr =  (p*boardingCost) - runningCost*moves;
            if(Math.abs(max) < Math.abs(curr)){
                max  = curr;
                ans = moves;
            }
            moves++;
        }
        while(balance > 0){
            int py = Math.min(balance, 4);
