class Solution {
public:
    int minOperationsMaxProfit(vector<int>& customers, int boardingCost, int runningCost) {
        int profit = 0, k = 0, n = customers.size();

        if(n < 1) {
            return -1;
        }

        for(int i = 0; i < n; i++) {
            if(i) {
                customers[i] += max(0, customers[i-1] - 4);
            }
            profit += min(4, customers[i]) * boardingCost - runningCost;
        }

        int c = max(0, customers.back() - 4);
        profit += (4 * boardingCost - runningCost) * (c / 4);

        if(c % 4 * boardingCost - runningCost > 0) {
            n++;
        }

        if(profit <= 0) {
            return -1;
        }

        return n + (c / 4);
    }
};
