class Solution:
    def minOperationsMaxProfit(self, customers: List[int], boardingCost: int, runningCost: int) -> int:
        # simulation of operations
        res = []
        remaining = 0
        nb_roation = 0
        total_boardingCost = 0

        while customers:
            nb_roation += 1
            if (customers[0] + remaining) <= 4:
                total_boardingCost += boardingCost * (customers[0] + remaining)
                remaining = 0
            else:
                total_boardingCost += boardingCost * 4
                remaining += customers[0] - 4
            res.append( total_boardingCost - runningCost * nb_roation)
            customers.pop(0)

        # clear waiting lines
        while remaining:
            nb_roation += 1
            if remaining >= 4:
                total_boardingCost += boardingCost * 4
                remaining -= 4
            else:
                total_boardingCost += boardingCost * remaining
                remaining = 0
            res.append( total_boardingCost - runningCost * nb_roation)

        if max(res) < 0:
            return -1
        else:
            return res.index(max(res)) + 1

