class Solution {
public:
    int minOperationsMaxProfit(vector<int>& customers, int boardingCost, int runningCost) {
        int n = customers.size(); if (n == 0) return -1;
        int waiting = 0, onboard  = 0, rotations = 0, maxcost = -1, i = 0, cost = 0, maxi;
        while (true) {
            if (i < n ) waiting += customers[i];
            int newonboarded = (waiting < 4)? waiting: 4;
            onboard += newonboarded;
            waiting -= newonboarded;
            i ++;
            cost = onboard * boardingCost - i * runningCost;
            if (cost > maxcost) {
                maxi = i;
                maxcost = cost;
            }
            if (waiting == 0 && i >= n) break;
        }
        return maxcost == -1 ? -1 : maxi;
    }
};
