class Solution {
    public int minOperationsMaxProfit(int[] customers, int boardingCost, int runningCost) {
        if (4 * boardingCost <= runningCost) {
            return -1;
        }
        int max = 0;
        int maxCnt = 0;
        int left = 0;
        int profit = 0;
        int cnt = 0;
        int i = 0;
        int n = customers.length;
        while (left > 0 || i < n) {
            if (i < n) {
                left += customers[i++];
            }
            int cur = Math.min(left, 4);
            left -= cur;
            ++cnt;
            profit += boardingCost * cur - runningCost;
            if (profit > max) {
                max = profit;
                maxCnt = cnt;
            }
        }
        return max > 0 ? maxCnt : -1;
    }
}
