class Solution {
    public int minOperationsMaxProfit(int[] customers, int boardingCost, int runningCost) {

        int maxProfit = 0;
        int waiting = 0;
        int i = 0;
        int index = 0;
        int profit = 0;
        int ans = 0;

        for(i=0; i<customers.length; i++){
            profit = 0;

            if(waiting > 0){
                customers[i] += waiting;
            }

            if(customers[i] > 4){
                waiting = customers[i] - 4;
                index = 4 * (i+1);
            }
            else{
                index = customers[i];
            }

            profit = index * boardingCost - (i+1) * runningCost;
            if(profit > maxProfit){
                maxProfit = profit;
                ans = i+1;
            }
        }

        while(waiting > 0){
            if(waiting <= 4){
                profit = (index+waiting) * boardingCost - (i+1) * runningCost;
                waiting = 0;
            }
            else{
                index += 4;
                profit = (index) * boardingCost - (i+1) * runningCost;
                waiting -= 4;
            }

            i++;
            if(profit > maxProfit){
                maxProfit = profit;
                ans = i;
            }
        }

        return maxProfit==0 ? -1 : ans;
    }
}
