class Solution {
public:
    int minOperationsMaxProfit(vector<int>& customers, int boardingCost, int runningCost) {

        int i=0, waitingCustomers = 0, boardedCustomers = 0, maximumProfit = INT_MIN, rotations = 0;
        while(i < customers.size() || waitingCustomers > 0)
        {
        	//Process customer at index i, and add them in waiting lists.
            if(i < customers.size())
                waitingCustomers += customers[i];

            i++;

            //If there are customers, process them.
            if(waitingCustomers > 0)
            {
            	//At a time max 4 can board
                int canBoard = (waitingCustomers >= 4 ? 4 : waitingCustomers);
                boardedCustomers += canBoard;
                waitingCustomers -= canBoard;

				//Calculate the profit.
                int profit = (boardedCustomers * boardingCost) - ( (i) *runningCost);
                if(maximumProfit < profit)
                {
                    maximumProfit = profit;
                    rotations = i;
                }                
            }
        }

        return maximumProfit < 0 ? -1 : rotations;
    }
};
