class Solution {
public:
    int minOperationsMaxProfit(vector<int>& customers, int boardingCost, int runningCost) {
        int waiting = 0, cost = 0, rounds = 0, maxCost = 0, maxRound = -1;
        for (int i = 0; i < customers.size(); i++)  {
            waiting+=customers[i];
            if (waiting >=4 ) {
                cost+=(4 * boardingCost);
                waiting-=4;
            } else {
                cost+=(waiting * boardingCost);
                waiting = 0;
            } 
            cost-=runningCost;
            rounds++;
            if (cost > maxCost) {
                maxRound = rounds;
                maxCost = cost;
