        while(waiting) {
            if (waiting >=4) {
                cost+=(4 * boardingCost);
                waiting-=4;         
            } else {
                cost+=waiting * boardingCost;
                waiting = 0;
            }  
            cost-=runningCost;
            rounds++;
            if (cost > maxCost) {
                maxRound = rounds;
                maxCost = cost; 
            } 
        }
        return maxRound; 
    }
};
