class Solution {
    public int minOperationsMaxProfit(int[] customers, int boardingCost, int runningCost) {
        int remain = 0, count = 0;
        int run = 0;
        int max = Integer.MIN_VALUE;
        List<Integer> res = new ArrayList<>();
        for (int i = 0; i < customers.length; i++) {
            remain += customers[i];
            if (remain >= 4) {
                remain -= 4;
                run += 4;
            } else {
                run += remain;
                remain = 0;
            }
            count++;

            int profit = boardingCost * run - runningCost * count;
            max = Math.max(max, profit);
            res.add(profit);
        }

        while (remain > 0) {
            if (remain >= 4) {
                remain -= 4;
                run += 4;
            } else {
                run += remain;
                remain = 0;
            }
            count++;
            int profit = boardingCost * run - runningCost * count;
            max = Math.max(max, profit);
            res.add(profit);

        }

        if (max < 0) return -1;

        for (int i = 0; i < res.size(); i++) {
            if (res.get(i) == max) {
                return i + 1;
            }
        }

        return 0;

    }
}
```

