public int MinOperationsMaxProfit(int[] customers, int boardingCost, int runningCost)
{
	int maxProfit = int.MinValue;
	int waitingCustomers = 0;
	int maxCapacity = 4;
	int rotationCount = 0;
	int result = 0;
	int profit = 0;

	for (int i = 0; i < customers.Length; i++)
	{
		rotationCount++;
		int currentCount;

		int customer = customers[i] + waitingCustomers;
		if (customer >= maxCapacity)
		{
			currentCount = maxCapacity;
			waitingCustomers = customer - maxCapacity;
		}
		else
		{
			currentCount = customer;
			waitingCustomers = 0;
		}

		profit += (currentCount * boardingCost) - runningCost;
		if (profit > maxProfit)
		{
			maxProfit = profit;
			result = rotationCount;
		}                
	}

	while (waitingCustomers > 0)
	{
		rotationCount++;
		int currentCount = waitingCustomers <= maxCapacity ? waitingCustomers : maxCapacity;
		waitingCustomers -= currentCount;

		profit += (currentCount * boardingCost) - runningCost;
		if (profit > maxProfit)
		{
			maxProfit = profit;
			result = rotationCount;
		}               
	}

	return maxProfit < 0 ? -1 : result;
}
