class Solution {
    public int minOperationsMaxProfit(int[] customers, int boardingCost, int runningCost) {

        int sum = 0;
        int profit = 0;
        int maxProfit = 0;
        int run = 0;
        int maxRun = 0;

        for(int i=0; i<customers.length; i++) {
            sum += customers[i];   // The total customers arrivied when i-th batch customers arrived.
            profit = Math.min(sum, 4 * (i+1)) * boardingCost - (i+1) * runningCost;  // After (i+1) rotations, at most 4*(i+1) customers can on board.
            run++;  // Wrong here! If there is no customer waiting, it needs no rotation.

            if(profit > maxProfit) {
                maxProfit = profit;
                maxRun = run;
            }
        }
        sum -= 4 * customers.length;

        while(sum >= 4) {
            profit += (4 * boardingCost - runningCost);
            sum -= 4;
            run++;
            if(profit > maxProfit) {
                maxProfit = profit;
                maxRun = run;
            }
        }
        if(sum > 0) {
            profit += (sum * boardingCost - runningCost);
            run++;
            if(profit > maxProfit) {
                maxProfit = profit;
                maxRun = run;
            }
        }
        return (maxProfit < 0) ? -1 : maxRun;
    }
}
