public class ThroneInheritance
{
	Dictionary<string, List<string>> childrens = new Dictionary<string, List<string>>();
	Dictionary<string, bool> isAliveMap = new Dictionary<string, bool>();
	string kingName;

	public ThroneInheritance(string kingName)
	{                
		childrens[kingName] = new List<string>();
		this.kingName = kingName;
		isAliveMap.Add(kingName, true);
	}

	public void Birth(string parentName, string childName)
	{                
		childrens[childName] = new List<string>();
		childrens[parentName].Add(childName);
		isAliveMap.Add(childName, true);
	}

	public void Death(string name)
	{                
		isAliveMap[name] = false;
	}

	public IList<string> GetInheritanceOrder()
	{
		List<string> result = new List<string>();

		if (isAliveMap[this.kingName])
		{
			result.Add(kingName);
		}

		addChildrens(this.kingName, result);
		return result;
	}


	private void addChildrens(string parent, List<string> result)
	{
		foreach (var children in childrens[parent])
