class ThroneInheritance:
    def __init__(self, kingName: str):
        #for keeping track of parents and their children
        self.people = {kingName:[]}

        #for keeping track of dead people
        self.dead = dict()

        #storing the king name
        self.kn = kingName

    def birth(self, parentName: str, childName: str) -> None:
        #if parent name already exists in our hashmap then append current childname to child list
        if self.people.get(parentName, 0) != 0:
            self.people[parentName].append(childName)

        #if parent name is not in our hashmap create new key value pair
        else:
            self.people[parentName] = [childName]

    def death(self, name: str) -> None:
        #when a dead person is reported we create a key value pair in this hashmap
        self.dead[name] = 1

    def getInheritanceOrder(self) -> List[str]:
        #for storing result
        res = []

        #Defining DFS function for finding all parents and their children 
        def dfs(curr, res):
            #if current person is dead then we don't append it to result
            if self.dead.get(curr, 0) == 0:
                res.append(curr)
            if curr in self.people.keys():
                for i in self.people[curr]:
                    dfs(i, res)

        #starting DFS from king name
        dfs(self.kn, res)
        return res
