```golang
type ThroneInheritance struct {
    king *member // root node
    nodeMap map[string]*member // node address map
    count int // total alive members in the family, just to optimize memory allocations in GetInheritanceOrder
}

type member struct {
    isDead bool
    name string
    children []*member
}

func newMember(name string) *member {
    return &member{name: name}
}

func Constructor(kingName string) ThroneInheritance {
    king := newMember(kingName)
    nodeMap := make(map[string]*member)
    nodeMap[kingName] = king
    return ThroneInheritance{king: king, nodeMap: nodeMap, count: 1}
}

func (this *ThroneInheritance) Birth(parentName string, childName string)  {
    child := newMember(childName)
    this.nodeMap[parentName].children = append(this.nodeMap[parentName].children, child)
    this.nodeMap[childName] = child
    this.count++
}

func (this *ThroneInheritance) Death(name string)  {
    this.nodeMap[name].isDead = true
    this.count--
}

func (this *ThroneInheritance) GetInheritanceOrder() []string {
    result := make([]string, 0, this.count)
    var preorder func(root *member)
    preorder = func(root *member) {
        if root == nil {
            return
        }
        if root.isDead == false {
            result = append(result, root.name)
        }
        for _, child := range root.children {
            preorder(child)
        }
    }
    preorder(this.king)
    return result
}
