class ThroneInheritance {

    ArrayList<String> curOrder;
    Set<String> deathSet;
    Map<String, List<String>> children;
    String king;
    Stack<String> stack = new Stack<String>();

    public ThroneInheritance(String kingName) {
        curOrder = new ArrayList<String>();
        stack.push(kingName);
        deathSet = new HashSet<String>();
        children = new HashMap<>();
        king = kingName;
    }

    public void birth(String parentName, String childName) {
        children.putIfAbsent(parentName, new ArrayList<String>());
        children.get(parentName).add(childName);
    }

    public void death(String name) {
        deathSet.add(name);
    }

    public List<String> getInheritanceOrder() {
        curOrder = new ArrayList<String>();
        while(!stack.isEmpty()) {
            String curr = stack.pop();
            if(!deathSet.contains(curr)) { curOrder.add(curr); }
            List<String> curArr = children.get(curr);
            if(curArr == null) { continue; }
            int n = curArr.size();
            for(int i = n-1; i >= 0; i--) {
                stack.push(curArr.get(i));
            }
        }
        stack.push(king);
        return curOrder;
    }
}
