```
class ThroneInheritance {

    Map<String, List<String>> map;
    String kingName;
    Set<String> deathList;
    public ThroneInheritance(String kingName) {
        this.kingName = kingName;
        deathList = new HashSet<>();
        map = new HashMap<>();
    }

    public void birth(String parentName, String childName) {
        if (!map.containsKey(parentName)) {
            map.put(parentName, new ArrayList<>());
        }
        map.get(parentName).add(childName);

    }

    public void death(String name) {      
        deathList.add(name);
    }

    public List<String> getInheritanceOrder() {
        return dfs(kingName, deathList);
    }


    public List<String> dfs(String name, Set<String> deathList) {
        List<String> path = new ArrayList<>();

        if (!deathList.contains(name))
            path.add(name);

        List<String> children = map.getOrDefault(name, new ArrayList<>());
        for (String child : children) {
            path.addAll(dfs(child, deathList));
        }

        return path;
    }
}
