class ThroneInheritance {
public:
    struct Node {

        // Keep track of name for inheritance order and hashing
        string name;

        // Keep track of children for n-ary tree traversal
        vector<Node*> children;

        // Marker indicating dead/alive
        bool alive;

        // Constructor
        Node(string name, vector<Node*> children, bool alive) {
            this->name = name;
            this->children = children;
            this->alive = alive;
        }
    };

    // Start of the n-ary family tree
    Node* root;

    // Hash map to quickly access nodes by name (1-to-1 mapping).
    // Names are assumed to be distinct in problem constraints.
    unordered_map<string, Node*> nameMap;

    ThroneInheritance(string kingName) {
        root = new Node(kingName, {}, true);
        nameMap[kingName] = root;
    }

    void birth(string parentName, string childName) {
        Node* parent = nameMap[parentName];
        Node* child = new Node(childName, {}, true);
        parent->children.push_back(child);
        nameMap[childName] = child;
    }

    void death(string name) {
        Node* timeToDie = nameMap[name];
        timeToDie->alive = false;
    }

    // Traverse the family tree with preOrder traversal (i.e. depth first search)
    vector<string> getInheritanceOrder() {
        stack<Node*> stack;
        vector<string> preOrder;
        stack.push(root);
        while (!stack.empty()) {
            Node* curr = stack.top();
            stack.pop();
            if (curr->alive) {
                preOrder.push_back(curr->name);
            }

            // Push all of the children into the stack from right to left.
            // (So the first born child is on top of the stack)
            for (int i = curr->children.size() - 1; i >= 0; i--) {
                stack.push(curr->children[i]);
            }
        }

        return preOrder;
    }                                                       
};

