class ThroneInheritance {
    private final Node root;
    private final Map<String, Node> map = new HashMap<>();

    public ThroneInheritance(String kingName) {
        root = new Node(kingName);
        map.put(kingName, root);
    }

    public void birth(String parentName, String childName) {
        Node parentNode = map.get(parentName);
        Node newChildNode = new Node(childName);
        map.put(childName, newChildNode);
        parentNode.children.add(newChildNode);;
    }

    public void death(String name) {
        Node node = map.get(name);
        node.isDead = true;
    }

    public List<String> getInheritanceOrder() {
        List<String> res = new ArrayList<>();
        LinkedList<Node> stack = new LinkedList<>();
        stack.add(root);
        while (!stack.isEmpty()) {
            Node node = stack.pollFirst();
            if (!node.isDead) {
                res.add(node.name);
            }
            int n = node.children.size();
            for (int i = n - 1; i >= 0; i--) {
                stack.addFirst(node.children.get(i));
            }
        }
        return res;
    }

    static class Node {
        public String name;
        public List<Node> children = new ArrayList<>();
        public boolean isDead;

        public Node(String name) {
            this.name = name;
        } 
    }
}
