class ThroneInheritance {

    class Royal {
        String name = "";
        boolean isAlive = true;
        List<Royal> children = new ArrayList<>();
        public Royal(String name) {
            this.name = name;
        }
    }

    Royal root;
    Map<String, Royal> royalFamily;

    public ThroneInheritance(String kingName) {
        root = new Royal(kingName);
        royalFamily = new HashMap<>();
        royalFamily.put(kingName, root);
    }

    public void birth(String parentName, String childName) {
        Royal member = royalFamily.get(parentName);
        Royal child = new Royal(childName);
        member.children.add(child);
        royalFamily.put(childName, child);
    }

    public void death(String name) {
        Royal member = royalFamily.get(name);
        member.isAlive = false;
    }

    public List<String> getInheritanceOrder() {
        List<String> res = new LinkedList<String>();
        traverse(root, res);
        return res;
    }

    private void traverse(Royal root, List<String> res) {
        if (root.isAlive) {
            res.add(root.name);
        }
        for (Royal child : root.children) {
            traverse(child, res);
        }
    }
}
