class ThroneInheritance {

    // Tells, node corresponding to the given 'name'. Used to make insertion fast in N-ary Tree.
    // It avoids performing DFS in N-ary Tree to find appropriate parent node to add a new child
    private Map<String, TreeNode> nodeAt;

    // Used to keep track of 'dead' people
    private Set<String> deadContainer;

    // Root of the N-ary Tree : To hold the entire Throne Hierarchy
    private TreeNode root;

    public ThroneInheritance(String kingName) {
        nodeAt = new HashMap<>();
        deadContainer = new HashSet<>();
        root = new TreeNode(kingName);
        nodeAt.put(kingName, root);
    }

    // Nothing but insertion of a child for the parent node(node_at[parentName]) in N-ary Tree
    public void birth(String parentName, String childName) {
        TreeNode parent = nodeAt.get(parentName);
        TreeNode child = new TreeNode(childName);
        parent.children.add(child);
        // Making a new mapping for current child in our map
        nodeAt.put(childName, child);
    }

    public void death(String name) {
        deadContainer.add(name); //make person with name 'name' as dead
    }

    // Throne Inheritance Order is nothing but data fetched in Pre-Order fashion in N-ary Tree
    //  By keeping in mind that 'dead' people are excluded.
    public List<String> getInheritanceOrder() {
        List<String> thorneInheritance = new ArrayList<>();
        preorderTraversal(thorneInheritance, root);
        return thorneInheritance;
    }

    // Store the preorder traversal of current N-ary into the given list 'thorneInheritance'
    // Exactly same as 'Preorder Traversal' in Binary Tree.
    // Our this pre-order traversal will automatically takes care the ordering of Throne Inheritance defined
    // in 'Successor(x, currOrder)'.
    public void preorderTraversal(List<String> thorneInheritance, TreeNode r) {
        // Reached to leaf node
        if(r.children.size() == 0) {
            if(!deadContainer.contains(r.name)) thorneInheritance.add(r.name);
            return;
        }

        if(!deadContainer.contains(r.name)) 
            thorneInheritance.add(r.name);

        for(TreeNode child : r.children) {
            if(child != null) {
                preorderTraversal(thorneInheritance, child);
            }
        }
    }

    // N-ary Tree's TreeNode strucutre
    private class TreeNode {
        String name; //name of the parent at current node
        List<TreeNode> children;

        TreeNode(String name) {
            this.name = name;
            this.children = new ArrayList<>();
        }

        TreeNode(String name, List<TreeNode> children) {
            this.name = name;
            this.children = children;
        }
    }
}

