 * @return {void}
 */
ThroneInheritance.prototype.death = function(name) {

    if (name === this.king) {
        this.familyGraph[name].isAlive = false;
        const dfs = (person) => {
            const kin = this.familyGraph[person];
            if (kin.isAlive) {
                this.king = person;
                return true;
            }
            for (let child of kin.children) {
                if (dfs(child)) {
                    return true;
                }
            }
        };

        dfs(this.firstKing);

