class Person:

    def __init__(self, name):
        self.name = name
        self.kids = []
        self.alive = True

class ThroneInheritance:

    def __init__(self, kingName: str):
        king = Person(kingName)
        self.head = king
        self.d = {kingName: king}


    def birth(self, parentName: str, childName: str) -> None:
        parent = self.d[parentName]
        child = Person(childName)
        self.d[childName] = child
        parent.kids.append(child)

    def death(self, name: str) -> None:
        self.d[name].alive = False

    def getInheritanceOrder(self) -> List[str]:
        stack = [self.head]
        res = []
        while stack:
            p = stack.pop()
            if p.alive: res.append(p.name)
            stack += p.kids[::-1]
        return res
