def death(self, name: str) -> None:
	self.deads.add(name)

def getInheritanceOrder(self) -> List[str]:
	stack = [self.king]
	order = []

	while stack:
		now = stack.pop()
		#print(now)
		if now not in self.deads:
			order.append(now)
		if now in self.child:
			stack+=self.child[now][::-1]
	return order
