class Node {
public:
    string val;
    vector<Node*> children;
    bool isDead = false;

    Node() {}

    Node(string _val) {
        val = _val;
    }

    Node(string _val, vector<Node*> _children) {
        val = _val;
        children = _children;
    }
};

class ThroneInheritance {
    stack<Node*>Q;
    unordered_map<string, Node*> nodeMap; // hashmap to store nodes address by names
    Node* root;
public:
    ThroneInheritance(string kingName) {
        root = new Node(kingName);
        nodeMap[kingName] = root;
    }

    void birth(string parentName, string childName) {
		// create a new node 
		// add this node to parents' children
		// store node's address in hashmap
        Node* newChild = new Node(childName);
        nodeMap[parentName]->children.push_back(newChild);
        nodeMap[childName] = newChild;
    }

    void death(string name) {
		// just assign isDead property to true
        nodeMap[name]->isDead = true;
    }

    vector<string> getInheritanceOrder() {
		// use pre-order traversal with stack
        vector<string> ans;
        Q.push(root);
        while(!Q.empty()){
            auto node = Q.top(); Q.pop();
			// if node is not dead push back to vector of ans
            if(!nodeMap[node->val]->isDead) ans.push_back(node->val);
            for(int i=node->children.size()-1; i >= 0; i--) 
                Q.push(node->children[i]);
        }

        return ans;
    }
};
