  class ThroneInheritance(king: String) {

    import scala.collection.mutable.{HashSet, HashMap, TreeSet, LinkedHashSet}
    var time = 0
    case class People(name: String, age: Int)
    val graph = new HashMap[String, TreeSet[People]]
    val parts = new HashMap[String, String]
    val dies  = new HashSet[String]

    graph.put(king, new TreeSet[People]()((a, b) => Integer.compare(a.age, b.age)))

    def birth(parentName: String, childName: String) {
      time += 1
      graph(parentName) += People(childName, time)
      graph.put(childName, new TreeSet[People]()((a, b) => Integer.compare(a.age, b.age)))
      parts.put(childName, parentName)
    }

    def death(name: String) = dies += name

    def getInheritanceOrder(): List[String] = {
      val gp = new HashMap[String, TreeSet[People]]
      graph.foreach {
        case (k, v) => {
          gp.put(k, new TreeSet[People]()((a, b) => Integer.compare(a.age, b.age)))
          v.foreach(p => gp(k) += p)
        }
      }
      val res = new LinkedHashSet[String]
      var x   = king

      def scs(x: String): String = {
        if (gp(x).isEmpty) return if (x == king) null else scs(parts(x))
        val oldest = gp(x).firstKey
        gp(x) -= oldest
        oldest.name
      }

      while (x != null) {
        res += x
        x = scs(x)
      }
      dies.foreach(x => res -= x)
      res.toList
    }

  }
