class ThroneInheritance {
    // node structure
    class TreeNode{
        String name;
        List<TreeNode> children;
        boolean isAlive;

        public TreeNode(String name){
            this.name = name;
            this.children = new ArrayList<TreeNode>();
            this.isAlive = true;
        }

        public void die(){
            this.isAlive = false;
        }

        public boolean isLiving(){
            return this.isAlive;
        }

        public List<TreeNode> getChildren(){
            return this.children;
        }

        public String getName(){
            return this.name;
        }
    }

    private TreeNode root;
    private HashMap<String, TreeNode> map;

    public ThroneInheritance(String kingName) {
        root = new TreeNode(kingName);
        map = new HashMap<String, TreeNode>();
        map.put(kingName, root);
    }

    public void birth(String parentName, String childName) {
        TreeNode node = map.get(parentName);
        TreeNode child = new TreeNode(childName);
        node.children.add(child);
        map.put(childName, child);
    }

    public void death(String name) {
        TreeNode node = map.get(name);
        node.die();
    }

    public List<String> getInheritanceOrder() {
        List<String> list = new ArrayList<String>();
        preorder(root, list);
        return list;
    }

    private void preorder(TreeNode node, List<String> list){
        if(node != null){
            if(node.isLiving()){
                list.add(node.getName());
            }

            for(TreeNode child : node.getChildren()){
                preorder(child, list);
            }    
        }
    }
}

