from collections import defaultdict

class ThroneInheritance:

    def __init__(self, kingName: str):
        self.kingName = kingName
        self.ft_dict = defaultdict(list) # Key as name; value as list of children
        self.is_alive = {kingName:True} # Key as name; value as is alive or not

    def birth(self, parentName: str, childName: str) -> None:
        self.ft_dict[parentName].append(childName)
        self.is_alive[parentName] = self.is_alive[childName] = True

    def death(self, name: str) -> None:
        self.is_alive[name] = False

    def getInheritanceOrder(self) -> List[str]:
        inheritance_order = []
        inheritance_stack = [self.kingName]
        while inheritance_stack:
            name = inheritance_stack.pop()
            if self.is_alive[name]:
                inheritance_order.append(name)

            inheritance_stack.extend(self.ft_dict[name][::-1])

        return inheritance_order
