class ThroneInheritance {
public:
    // Construct a family tree and populate on each birth.
    // Maintain a name->node index for faster lookup on birth/death.
    // On death, mark node as dead, but don't remove them.
    // Return pre-order traversal of names to get inheritance order.

    // This method optimizes birth/death to be const time operations.
    struct Node {
        Node(string name): name(move(name)) {}
        Node() = default;

        string name;
        // Head and tail of children
        struct Node* chead = nullptr;
        struct Node* ctail = nullptr;

        // Next sibling.
        struct Node* snext = nullptr;

        bool is_dead = false;
    };
    unordered_map<string, Node*> index_;
    Node* root_ = nullptr;

    // Number of live nodes. To reserve space for inheritance order vector.
    int live_nodes_ = 0;

    ThroneInheritance(string kingName) {
        root_ = new Node(kingName);
        index_.emplace(move(kingName), root_);
        live_nodes_ = 1;
    }

    void birth(string parentName, string childName) {
        auto it = index_.find(parentName);
        assert(it != index_.end());
        auto parent = it->second;

        auto child = new Node(childName);
        index_.emplace(move(childName), child);
        live_nodes_++;

        if (parent->chead == nullptr) {
            parent->chead = parent->ctail = child;
        } else {
            parent->ctail->snext = child;
            parent->ctail = child;
        }
    }

    void death(string name) {
        auto it = index_.find(name);
        assert(it != index_.end());
        auto node = it->second;
        node->is_dead = true;
        live_nodes_--;
    }

    vector<string> getInheritanceOrder() {
        vector<string> res;
        if (!root_ || live_nodes_ == 0) return res;

        res.reserve(live_nodes_);
        dfs(root_, res);
        return res;
    }

    void dfs(Node* root, vector<string>& res) {
        if (!root->is_dead) {
            res.push_back(root->name);
        }

        for (auto child = root->chead; child; child = child->snext) {
            dfs(child, res);
        }
    }
};
