class ThroneInheritance {
public:
    ThroneInheritance(string kingName) {
        king_name = kingName;
        succession[kingName] = {};
    }

    void birth(string parentName, string childName) {
        succession[parentName].push_back(childName);
    }

    void death(string name) {
        dead_people.insert(name);
    }

    vector<string> getInheritanceOrder() {
        vector<string> order;

        dfs(order, succession, king_name);

        return order;
    }

    void dfs(vector<string>& order, unordered_map<string, vector<string>>& succession, string curr_name) {
        if (dead_people.find(curr_name) == dead_people.end()) order.push_back(curr_name);

        for (string children : succession[curr_name]) {
            dfs(order, succession, children);
        }        
    }

private:
