void solve(vector<vector<int>>& nums, int ind, int accept, vector<int>& cnt)
{
    // Check if all the requests have been processed
    if(ind==nums.size())
    {
        // Check if all the counters in 'cnt' are zero, indicating a valid solution
        for(auto x: cnt) if(x!=0) return;

        // Update the maximum number of requests accepted if 'accept' is greater
        ans = max(ans,accept);

        // Exit the current recursive call
        return;
    }

    // Decrease the counter for the source vertex of the current request
    cnt[nums[ind][0]]--;

    // Increase the counter for the destination vertex of the current request
    cnt[nums[ind][1]]++;

    // Recursively call the solve function with the next request, incrementing 'accept'
    solve(nums,ind+1,accept+1,cnt);

    // Restore the counters to their original state
    cnt[nums[ind][0]]++;
    cnt[nums[ind][1]]--;

    // Recursively call the solve function with the next request, without incrementing 'accept'
    solve(nums,ind+1,accept,cnt);

    // Exit the current recursive call
    return;
}

