class Solution {
    public int f(int i, int[][] requests, int[] freq, int count) {
        if(i == requests.length) {
            for(int j: freq) if(j != 0) return 0; // net transfer != 0
            return count; // net tranasfer == 0
        }

        int notTake = f(i + 1, requests, freq, count);

        freq[requests[i][0]]--; freq[requests[i][1]]++; // Update the freq
        int take = f(i + 1, requests, freq, count + 1);
