class Solution {
public:
    int maximumRequests(int n, vector<vector<int>>& requests) {
        int maxRequests = 0;
        int numRequests = requests.size();

        // Generate all possible combinations of requests
        for (int mask = 0; mask < (1 << numRequests); mask++) {
            vector<int> buildings(n, 0);  // Tracks net change for each building

            // Count the number of requests in this combination
            int count = 0;

            for (int i = 0; i < numRequests; i++) {
                if (mask & (1 << i)) {
                    // Include this request in the combination
                    count++;
                    int from = requests[i][0];
                    int to = requests[i][1];
                    buildings[from]--;
                    buildings[to]++;
                }
            }

            // Check if the combination results in a net change of zero for all buildings
            bool isValid = true;
            for (int b : buildings) {
                if (b != 0) {
                    isValid = false;
                    break;
                }
            }

            // Update the maximum number of achievable requests
            if (isValid) {
                maxRequests = std::max(maxRequests, count);
            }
        }

        return maxRequests;
    }
};

